class MLKemDecapsTest : public IcicleTestBase
{
public:
  static const size_t BATCH_SIZE;

  void SetUp() override
  {
    IcicleTestBase::SetUp();
    std::cout << "\nRunning tests with batch size: " << BATCH_SIZE << std::endl;
  }

  template <typename T>
  static void randomize(T* arr, uint64_t size)
  {
    // Fill the array with random values
    uint32_t* u32_arr = (uint32_t*)arr;
    for (int i = 0; i < (size * sizeof(T) / sizeof(uint32_t)); ++i) {
      u32_arr[i] = rand_uint_32b();
    }
  }

  static std::string voidPtrToHexString(const std::byte* byteData, size_t size)
  {
    std::ostringstream hexStream;
    for (size_t i = 0; i < size; ++i) {
      hexStream << std::hex << std::setw(2) << std::setfill('0') << static_cast<int>(byteData[i]);
    }
    return hexStream.str();
  }

  template <const uint8_t k, const uint8_t eta1, const uint8_t eta2, const uint8_t du, const uint8_t dv>
  void decaps_test(const uint8_t* reference_dk, const uint8_t* reference_c, const uint8_t* reference_K_prime)
  {
    const size_t dk_size = 768 * k + 96;
    const size_t c_size = 32 * (du * k + dv);
    const size_t K_size = 32;

    uint8_t* d_dk;
    uint8_t* d_c;
    uint8_t* d_K_prime;
    Zq* d_A;

    const int BATCH_SIZE = 1 << 13;

    // Allocate memory for inputs and outputs
    cudaMalloc(&d_dk, dk_size * BATCH_SIZE);
    cudaMalloc(&d_c, c_size * BATCH_SIZE);
    cudaMalloc(&d_K_prime, K_size * BATCH_SIZE);
    cudaMalloc(&d_A, PolyMatrixView<256, k, k, Zq>::byte_size() * BATCH_SIZE);

    // Copy inputs from host to device
    for (int i = 0; i < BATCH_SIZE; ++i) {
      cudaMemcpy(d_dk + i * dk_size, reference_dk, dk_size, cudaMemcpyHostToDevice);
      cudaMemcpy(d_c + i * c_size, reference_c, c_size, cudaMemcpyHostToDevice);
    }

    cudaError_t err = cudaDeviceSynchronize();
    if (err != cudaSuccess) { printf("Copy failed with error \"%s\"\n", cudaGetErrorString(err)); }

    // Launch kernel
    ml_kem_decaps_kernel<k, eta1, eta2, du, dv><<<BATCH_SIZE, 128>>>(d_dk, d_c, d_K_prime, d_A);

    err = cudaDeviceSynchronize();
    if (err != cudaSuccess) { printf("Kernel launch failed with error \"%s\"\n", cudaGetErrorString(err)); }

    uint8_t h_K_prime[K_size];

    // Check results
    for (int i = 0; i < BATCH_SIZE; ++i) {
      cudaMemcpy(h_K_prime, d_K_prime + i * K_size, K_size, cudaMemcpyDeviceToHost);
      err = cudaDeviceSynchronize();
      if (err != cudaSuccess) { printf("Copy result failed with error \"%s\"\n", cudaGetErrorString(err)); }

      for (int j = 0; j < K_size; ++j) {
        ASSERT_EQ(h_K_prime[j], reference_K_prime[j]) << "K' check mismatch: i = " << i << " j = " << j;
      }
    }

    cudaFree(d_dk);
    cudaFree(d_c);
    cudaFree(d_K_prime);
    cudaFree(d_A);
  }
};

